import React, { useState } from "react";
import Modal from "./Modal";
import './SaginawCards.css'

function SaginawCard({ card }) {
  const [open, setOpen] = useState(false);

  return (
    <>
      <div className="SaginawCard" tabIndex="0" onClick={() => setOpen(true)}>
        <img className="CardImage" src={card.image} alt={card.description} />
        <div className="overlay">
          <div>
            <div className="subtitle">{card.description}</div>
          </div>
        </div>
      </div>

      <Modal isOpen={open} onClose={() => setOpen(false)}>
        <img className="CardImage" src={card.image} alt={card.description} />
        <div className="overlay">
          <div>
            <div className="subtitle">{card.description}</div>
          </div>
        </div>
      </Modal>
    </>
  );
}

export default SaginawCard;
